import React, { useState } from "react";
import { connect } from "react-redux";
import { Link, Redirect } from "react-router-dom";
import { empcreate } from "../Actions/Auth";

function AddEmp({ empcreate, isAuthenticated }) {
  const [empcreated, setempcreated] = useState(false);

  const [Formdata, setFormdata] = useState({
    name: "",
    first_dose: "",
    second_dose: "",
    both: "",
    not_both: "",
  });


  const [Checked, setChecked] = useState(false)

  // const handleclicks = (e) => {
  //   setChecked(true,{...Formdata,[e.target.name]: e.target.value})
  // }
  console.log(Checked)



  const { name, first_dose, second_dose, both, not_both } = Formdata;

  const onChange = (e) =>
    setFormdata({ ...Formdata, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    empcreate(name, first_dose, second_dose, both, not_both);
    setempcreated(true);
  };

  if (empcreated) {
    return <Redirect to="/emp" />;
  }

  return (
    <div className="container mt-5">
      <h1>Add employee details</h1>
      <h1> checkbox is ; {Checked ? "tr" : "fl"}</h1>

      <form onSubmit={(e) => onSubmit(e)}>
        <div className="form-group">
          <input
            className="form-control"
            type="text"
            placeholder="Employee name*"
            name="name"
            value={name}
            onChange={(e) => onChange(e)}
            required
          />
        </div>
        <br/>
        <div class="form-check">
          <input
            className="form-check-input"
            type="checkbox"
            name="first_dose"
            value = {first_dose}
            defaultChecked = {Checked}
            onChange={(e) => {setChecked(...Checked,e.target.Checked)}}
            id="flexCheckDefault"
          />
          <label className="form-check-label" for="flexCheckDefault">
            First Dose
          </label>
        </div>
        <div class="form-check">
          <input
            className="form-check-input"
            type="checkbox"
            value=""
            id="flexCheckDefault"
          />
          <label className="form-check-label" for="flexCheckDefault">
            Second Dose
          </label>
        </div>
        <div class="form-check">
          <input
            className="form-check-input"
            type="checkbox"
            value=""
            id="flexCheckDefault"
          />
          <label className="form-check-label" for="flexCheckDefault">
            fully vaccinated
          </label>
        </div>
        <div class="form-check">
          <input
            className="form-check-input"
            type="checkbox"
            value=""
            id="flexCheckDefault"
          />
          <label className="form-check-label" for="flexCheckDefault">
            not yet vaccinated
          </label>
        </div>


        <button className="btn btn-primary" type="submit">
          Register
        </button>
      </form>
    </div>
  );
}

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});
export default connect(mapStateToProps, { empcreate })(AddEmp);
